package Academy.AgainSomething;

public class WhileDemo266 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//While loop
		
		int i=0;
		while(i<10) //This boolean, while is true, goes into the block of execution
		{
			System.out.println(i);
			i++;
		}
		
		
		//Do while - one loop of execution is guaranteed
		int j=20;
		
		do
		{
			System.out.println(j);
			j++;
			
		}while(j<30);

	}

}
